package com.paul;
import java.util.HashMap;
import java.util.Random;

import com.paul.Analyzer.Analysis;

public class Main {

	public static void main(String[] args) {
		final Random r = new Random();
		
		String s = "";
		
		for (int i = 0; i <= 100000; i++) {
			s += r.nextBoolean() ?  "1" : "0";
		}
		
		
		Analyzer a = new Analyzer(test);
		
		for (int size = 1; size <= 4; size++) {
			a.run(size);
		}
		
		for (Analysis run : a.runs) {
			System.out.println(run.size + " " + 100 * run.averageRelDiff() + "%");
		}
		
		System.out.println();
		System.out.println();
		
		Analysis analysis = a.getAnalysisBySize(4);
		
		for (String key : analysis.values.keySet()) {
			System.out.println(key + ": " + analysis.values.get(key));
		}
	}
	
	public static String test = "";
}
